import{_ as d}from"./getTextbooks.3c0e3d7a.js";import{a as c,b as t,e as v,F as s,f as i,o as e}from"./entry.1072fb68.js";const f={},n=i('<div class="box" data-v-f0c08463><div class="top-block" data-v-f0c08463><div class="text-box" data-v-f0c08463><div class="text1" data-v-f0c08463>校園與職場霸凌層出不窮</div><div class="text2" data-v-f0c08463>在現在網路普及的時代，人與人之間的衝突，<br data-v-f0c08463> 已成更難解的習題。</div></div><div class="background" data-v-f0c08463></div></div><div class="top-over" data-v-f0c08463><div class="layer" data-v-f0c08463></div></div><div class="second-block" data-v-f0c08463><div class="item item1" data-v-f0c08463><div class="text-box" data-v-f0c08463><div class="text1" data-v-f0c08463>設計理念</div><div class="text2" data-v-f0c08463>受到少子化的影響，學生在家中少有跟手足相處的機會，來到<br data-v-f0c08463> 學校才開始學習跟同儕互動，面對自己的需求，用本能的生存<br data-v-f0c08463> 姿態在應對，學生間一定會有衝突，學生們也在衝突間學習如<br data-v-f0c08463> 何跟他人相處。但若無師長的正向引導，任由同儕間經常發生<br data-v-f0c08463> 衝突，久了就容易形成霸凌。</div></div><div class="background bg1" data-v-f0c08463></div></div><div class="item" data-v-f0c08463><div class="background bg2" data-v-f0c08463></div><div data-v-f0c08463><div class="text-box" data-v-f0c08463><div class="text3" data-v-f0c08463>近幾年教育現場不斷在推動「反霸凌」，但是缺乏好操作的教<br data-v-f0c08463> 材供老師使用，老師們僅能依賴自己的經驗，卻經常心有餘而<br data-v-f0c08463> 力不足，因此阿普蛙設計了這套教材，我們期待從根本來解決<br data-v-f0c08463> 問題，霸凌者之所以為霸凌者，往往是因為他們想要滿足自身<br data-v-f0c08463> 需求，卻使用的錯誤的方式來滿足，而造成對他人的傷害而不<br data-v-f0c08463>自知。</div></div></div></div></div><div class="second-over" data-v-f0c08463><div class="layer" data-v-f0c08463></div></div></div><div class="third-block" data-v-f0c08463><div class="content-box" data-v-f0c08463><div class="title" data-v-f0c08463>關於教材</div><div class="text" data-v-f0c08463>本教材將以線上形式呈現作為使用，教材將教導學生認識人的「需求」， 並帶著學生討論及教導他們「適當」滿足需求的方式，<br data-v-f0c08463>我們不只教導孩子方法，我們亦在培養孩子自我覺察及關懷他人的能力，霸凌的發生，往往就是因為有許多冷漠或害怕的旁觀<br data-v-f0c08463> 者，而使得衝突演變成霸凌，我們期待孩子能夠成為正義的助人者，共創和諧的班級文化。</div><div class="item-box" data-v-f0c08463><div class="item" data-v-f0c08463><div class="background" data-v-f0c08463></div><div class="item-title" data-v-f0c08463>虎兔篇</div><div class="item-content" data-v-f0c08463>潛在霸凌者需求:生理需求</div><div class="item-btn" data-v-f0c08463><span class="btn-span" data-v-f0c08463>劇本介紹</span><span class="arrowIcon" data-v-f0c08463>➔</span></div></div><div class="item" data-v-f0c08463><div class="background" data-v-f0c08463></div><div class="item-title" data-v-f0c08463>虎兔篇</div><div class="item-content" data-v-f0c08463>潛在霸凌者需求:生理需求</div><div class="item-btn" data-v-f0c08463><span class="btn-span" data-v-f0c08463>劇本介紹</span><span class="arrowIcon" data-v-f0c08463>➔</span></div></div><div class="item" data-v-f0c08463><div class="background" data-v-f0c08463></div><div class="item-title" data-v-f0c08463>虎兔篇</div><div class="item-content" data-v-f0c08463>潛在霸凌者需求:生理需求</div><div class="item-btn" data-v-f0c08463><span class="btn-span" data-v-f0c08463>劇本介紹</span><span class="arrowIcon" data-v-f0c08463>➔</span></div></div><div class="item" data-v-f0c08463><div class="background" data-v-f0c08463></div><div class="item-title" data-v-f0c08463>虎兔篇</div><div class="item-content" data-v-f0c08463>潛在霸凌者需求:生理需求</div><div class="item-btn" data-v-f0c08463><span class="btn-span" data-v-f0c08463>劇本介紹</span><span class="arrowIcon" data-v-f0c08463>➔</span></div></div></div><div class="content" data-v-f0c08463>每篇長度不同( 2-5 天版本)，可依教學需求選擇。劇本會以每天發布一段故事的方式呈現，並附有選項讓學生回家跟家長討論，隔日回到班上後再和同<br data-v-f0c08463> 組成員做出選擇。每個選項會有各自的計分，引導劇本走向不同結局。進入結局後，會由老師帶領學生進行最終討論。</div></div></div>',2);function l(o,r){const a=d;return e(),t(s,null,[n,v(a)],64)}const m=c(f,[["render",l],["__scopeId","data-v-f0c08463"]]);export{m as default};
